import{_ as p}from"./chunks/ArticleMetadata.Sb1DYAHo.js";import{_ as b,D as m,o as a,c as h,I as n,w as l,k as s,a as u,R as i,b as _,e as c}from"./chunks/framework.FVQzxbLi.js";import"./chunks/md5.RtphNWHi.js";const V=JSON.parse('{"title":"1.5数字化转型与元宇宙","description":"","frontmatter":{"title":"1.5数字化转型与元宇宙","author":"吴建华","date":"2024/3/3 22:09","categories":["软考"],"tags":["信息化发展","软考"]},"headers":[],"relativePath":"ruankao/01信息化发展/05数字化转型与元宇宙.md","filePath":"ruankao/01信息化发展/05数字化转型与元宇宙.md","lastUpdated":1709477591000}'),f={name:"ruankao/01信息化发展/05数字化转型与元宇宙.md"},D=s("h1",{id:"_1、数字化转型",tabindex:"-1"},[u("1、数字化转型 "),s("a",{class:"header-anchor",href:"#_1、数字化转型","aria-label":'Permalink to "1、数字化转型"'},"​")],-1),g=i('<table><tr style="background-color:#DBEDF4;text-align:center;"><td></td><td>要素</td><td colspan="3">具体内容</td></tr><tr><th rowspan="8">数 字 化 转 型</th><td>定义</td><td colspan="3">数字化转型是建立在数字化转换(Digitization)、数字化升级(Digitalization)基础上，进一步 触及组织核心业务，以新建一种业务模式为目标的高层次转型。只有组织对其业务进行系统性、 彻底的(或重大和完全的)重新定义，而不仅仅是IT,而是对组织活动、流程、业务模式和员工能力的方方面面进行重新定义的时候，成功才会得以实现。</td></tr><tr><td>驱动 因素</td><td colspan="3">1)生产力飞升：第四次科技革命：第一科学范式为经验范式第二科学范式为理论范式。第三科学范式为模拟范式。第四科学范式为数据密集型研究范式<br> 2)生产要素变化：数据要素的诞生：数据是与土地、劳动力、资本和技术并列的主要生产要素 表明数据将会是未来社会数字化、智能化发展的重要基础。<br> 3)信息传播效率突破：社会互联网新格局，社交网络信息传输具有永生性、无限性、即时性以及方向性的特征。<br> 4)社会“智慧主体”规模：快速复制与“智能+&#39;<br></td></tr><tr><td>基本 原理</td><td colspan="3">1)能力因子定义和数字化“ 封 装 ”<br> 2)基于“互联网+”的调度和决策组织数字化转型中的难点，主要体现在：业务融合、持续坚持、 文化冲突、效果判别<br> 3)转型控制<br></td></tr><tr><th rowspan="4">智慧 转移</th><td colspan="3">1.个体智慧(知识、技能和经验等)由“自然人”个体转移到组织智慧(计算机、信息系统等掌 握的)<br> 2.DIKW模型很好地诠释了数据(Data)、信息(Information)、知识(Knowledge)和智慧 (Wisdom)之间的关系，并揭示了他们的转化过程与方法。<br> 3.S&amp;D模型就是基于DIKW模型，构筑了“智慧-数据”“数据-智慧”两大过程的8个转化活动</td></tr><tr style="text-align:center;"><td>转化活动</td><td>过程</td><td style="text-align:center;">内容要点</td></tr><tr><td>“智慧-数 据”过程</td><td>信息系统规划、 建设、运行过程；“信息化过 程 ”</td><td>①通过智慧结构化明确了业务体系层面的内容；②通过知识模型化 定义业务活动的逻辑关系；③通过过程信息化(管理和工艺流程化) 明确各执行操作系列要求；④通过数据平台化实现了数据的采集、 存储和共享等。</td></tr><tr><td>“数据-智 慧”过程</td><td>数据的开发利用 和资源管理的过 程 ；“智慧化过 程 ”</td><td>重点解决基于各类组织组成对象(人员、流程、业务、工艺、装备 等)“数字关系”的“脑力替代”①通过对象数字化实现对各类对象 的数字化表达；②通过孪生虚拟化完成物理对象到信息空间的映 射；③通过架构可视化实现业务知识模型与经验沉淀的复用和仓 新；④通过计算智能化实现多元条件下的调度和决策</td></tr><tr><td>持续 迭代</td><td colspan="3">1.组织数字化转型需要在能力因子不断细化的基础上，针对能力因子的数字化转型实施迭代，可 类比为整体数字化转型与局部数字转型的关系。<br> 2.组织每个能力因子数字化“封装”的持续迭代主要包含四项活动，即：信息物理世界(也称教 字孪生，CPS)建设、决策能力边际化(PtoE) 部署、科学社会物理赛博机制构筑(CPSS)、 数字框架与信息调制(DFIM)。</td></tr></table><h1 id="_2、元宇宙" tabindex="-1">2、元宇宙 <a class="header-anchor" href="#_2、元宇宙" aria-label="Permalink to &quot;2、元宇宙&quot;">​</a></h1>',2),k=i('<table><tr style="background-color:#DBEDF4;text-align:center;"><td></td><td>要素</td><td colspan="2">具体内容</td></tr><tr><th rowspan="6">云宇宙</th><td>定义</td><td colspan="2">宇宙(Metaverse)是一大批技术的集成。<br> →陈刚教授的定义：元宇宙是利用科技手段进行链接与创造的，与现实世界映射与交互的虚拟世界，具备新型社会体系的数字生活空间。<br> &gt;沈阳教授的定义：元宇宙是整合多种新技术而产生的新型虚实相融的互联网应用和社会形态它基于扩展现实技术提供沉浸式体验，以及数字孪生技术生成现实世界的镜像，通过区块链技术 搭建经济体系，将虚拟世界与现实世界在经济系统、社交系统、身份系统上密切融合，并且允许每个用户进行内容生产和编辑。<br> →左鹏飞从时空性、真实性、独立性、连接性四个方面去交叉定义元宇宙。<br> ◆从时空性来看，元宇宙是一个空间维度上虚拟而时间维度上真实的数字世界；<br> ◆从真实性来看，元宇宙中既有现实世界的数字化复制物，也有虚拟世界的创造物；<br> ◆从独立性来看，元宇宙是一个与外部真实世界既紧密相连，又高度独立的平行空间；<br> ◆从连接性来看，元宇宙是一个把网络、硬件终端和用户囊括进来的一个永续的、广覆盖的虚拟 现实系统。<br></td></tr><tr style="background-color:#DBEDF4;text-align:center;"><td></td><td>主要特征</td><td>解释说明</td></tr><tr><th rowspan="4">主要特征</th><td>沉浸式体验</td><td>元宇宙的发展主要基于人们对互联网体验的需求，这种体验就是即时信息基础上的沉浸式体验。</td></tr><tr><td>虚拟身份</td><td>人们已经拥有大量的互联网账号，未来人们在元宇宙中，随着账号内涵和外延的进 一步丰富，将会发展成为一个或若干个数字身份，这种身份就是数字世界的一个或一组角色 。</td></tr><tr><td>虚拟社会治 理</td><td>元宇宙中的经济与社会活动也需要一定的法律法规和规则的约束，就像现实世界-样，元宇宙也需要社区化的社会治理。</td></tr></table>',1);function T(t,S,C,P,I,$){const d=p,o=m("ClientOnly");return a(),h("div",null,[D,n(o,null,{default:l(()=>{var r,e;return[(((r=t.$frontmatter)==null?void 0:r.aside)??!0)&&(((e=t.$frontmatter)==null?void 0:e.showArticleMetadata)??!0)?(a(),_(d,{key:0,article:t.$frontmatter},null,8,["article"])):c("",!0)]}),_:1}),g,n(o,null,{default:l(()=>{var r,e;return[(((r=t.$frontmatter)==null?void 0:r.aside)??!0)&&(((e=t.$frontmatter)==null?void 0:e.showArticleMetadata)??!0)?(a(),_(d,{key:0,article:t.$frontmatter},null,8,["article"])):c("",!0)]}),_:1}),k])}const N=b(f,[["render",T]]);export{V as __pageData,N as default};
